import { Component } from '@angular/core';
import Swal from 'sweetalert2';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Sistema gestión de empleados';
  showAlert() {
    Swal.fire({
      title: '¡Hola!',
      text: 'Esto es una alerta con SweetAlert2 en Angular',
      icon: 'success',
      confirmButtonText: 'Cerrar'
    });
  }
}
<nav class="navbar navbar-expand-sm bg-dark navbar-dark">
  <ul class="navbar-nav">
    <li class="nav-item">
      <a routerLink="empleados" routerLinkActive="active" class="nav-link" >Lista de empleados</a>
    </li>
    <li class="nav-item">
      <a routerLink="registrar-empleado" routerLinkActive="active" class="nav-link" >Añadir empleado</a>
    </li>
  </ul>
</nav>

<h1 class="text-center">{{title}}</h1>
<div class="container">
  <router-outlet></router-outlet>
</div>
