import  swal  from 'sweetalert2';
import { EmpleadoService } from './../empleado.service';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Empleado } from '../empleado';

@Component({
  selector: 'app-empleado-detalles',
  templateUrl: './empleado-detalles.component.html',
  styleUrls: ['./empleado-detalles.component.css']
})
export class EmpleadoDetallesComponent implements OnInit {

  id:number;
  empleado:Empleado;
  constructor(private route:ActivatedRoute,private empleadoServicio:EmpleadoService) { }

  ngOnInit(): void {
    this.id = this.route.snapshot.params['id'];
    this.empleado = new Empleado();
    this.empleadoServicio.obtenerEmpleadoPorId(this.id).subscribe(dato => {
      this.empleado = dato;
      swal.fire(`Detalles del empleado ${this.empleado.nombre}`);
    });
  }

}
<h3>Detalles del empleado</h3>
<div>
  <div>
    <label><b>ID : </b> {{ empleado.id }}</label>
  </div>
  <div>
    <label><b>Nombre : </b> {{ empleado.nombre }}</label>
  </div>
  <div>
    <label><b>Apellido : </b> {{ empleado.apellido }}</label>
  </div>
  <div>
    <label><b>Email : </b> {{ empleado.email }}</label>
  </div>
</div>
