import { Component, OnInit } from '@angular/core';

import { Aula } from '../aula';
import { AulaService } from '../aula.service';
import { MessageService } from '../message.service';

@Component({
  selector: 'app-aulas',
  templateUrl: './aulas.component.html',
  styleUrls: ['./aulas.component.css']
})
export class AulasComponent implements OnInit {

  selectedAula?: Aula;

  aulas: Aula[] = [];

  constructor(private aulaService: AulaService, private messageService: MessageService) { }

  ngOnInit(): void {
    this.getAulas();
  }

  onSelect(aula: Aula): void {
    this.selectedAula = aula;
    this.messageService.add(`AulasComponent: Selected aula id=${aula.id}`);
  }

  getAulas(): void {
    this.aulaService.getAulas()
        .subscribe(aulas => this.aulas = aulas);
  }
}
<h2>AULAS</h2>

<ul class="aulas">
  <li *ngFor="let aula of aulas">
    <button [class.selected]="aula === selectedAula" type="button" (click)="onSelect(aula)">
      <span class="badge">{{aula.id}}</span>
      <span class="name">{{aula.name}}</span>
    </button>
  </li>
</ul>

<app-aula-detail [aula]="selectedAula"></app-aula-detail>
