import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Location } from '@angular/common';

import { Aula } from '../aula';
import { AulaService } from '../aula.service';

@Component({
  selector: 'app-aula-detail',
  templateUrl: './aula-detail.component.html',
  styleUrls: [ './aula-detail.component.css' ]
})
export class AulaDetailComponent implements OnInit {
  aula: Aula | undefined;

  constructor(
    private route: ActivatedRoute,
    private aulaService: AulaService,
    private location: Location
  ) {}

  ngOnInit(): void {
    this.getAula();
  }

  getAula(): void {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.aulaService.getAula(id)
      .subscribe(aula => this.aula = aula);
  }

  goBack(): void {
    this.location.back();
  }
}
<div *ngIf="aula">

    <h2>{{aula.name | uppercase}} Detalles</h2>
    <div><span>id: </span>{{aula.id}}</div>
    <div>
      <label for="aula-name">Nombre aula: </label>
      <input id="aula-name" [(ngModel)]="aula.name" placeholder="Nombre aula">
    </div>
    <button type="button" (click)="goBack()">
      atrás</button>
  </div>
