import { Component, OnInit } from '@angular/core';

import { Aula } from '../aula';
import { AulaService } from '../aula.service';

@Component({
  selector: 'app-aulas',
  templateUrl: './aulas.component.html',
  styleUrls: ['./aulas.component.css']
})
export class AulasComponent implements OnInit {
  aulas: Aula[] = [];

  constructor(private aulaService: AulaService) { }

  ngOnInit(): void {
    this.getAulas();
  }

  getAulas(): void {
    this.aulaService.getAulas()
    .subscribe(aulas => this.aulas = aulas);
  }

  add(name: string): void {
    name = name.trim();
    if (!name) { return; }
    this.aulaService.addAula({ name } as Aula)
      .subscribe(aula => {
        this.aulas.push(aula);
      });
  }

  delete(aula: Aula): void {
    this.aulas = this.aulas.filter(h => h !== aula);
    this.aulaService.deleteAula(aula.id).subscribe();
  }

}
<h2>Aulas</h2>

<div>
  <label for="new-aula">Nombre aula: </label>
  <input id="new-aula" #aulaName />

  <!-- (click) passes input value to add() and then clears the input -->
  <button type="button" class="add-button" (click)="add(aulaName.value); aulaName.value=''">
    Añadir aula
  </button>
</div>

<ul class="aulas">
  <li *ngFor="let aula of aulas">
    <a routerLink="/detail/{{aula.id}}">
      <span class="badge">{{aula.id}}</span> {{aula.name}}
    </a>
    <button type="button" class="delete" title="delete aula"
      (click)="delete(aula)">x</button>
  </li>
</ul>
