import { Component, Input } from '@angular/core';
import { Aula } from '../aula';

@Component({
  selector: 'app-aula-detail',
  templateUrl: './aula-detail.component.html',
  styleUrls: ['./aula-detail.component.css']
})
export class AulaDetailComponent {
  @Input() aula?: Aula;
}
<div *ngIf="selectedAula">
    <h2>{{selectedAula.name | uppercase}} Detalles</h2>
    <div>id: {{selectedAula.id}}</div>
    <div>
      <label for="aula-name">Nombre aula: </label>
      <input id="aula-name" [(ngModel)]="selectedAula.name" placeholder="name">
    </div>
  </div>
